class Solution {
public:
    int minChanges(vector<int>& nums, int k) {


 map<int, vector<int>> mppDi;

        vector<int> valXv;


        int n = nums.size();

        for (int i = 0; i < n / 2; i++) {

            int a = nums[i];

            int b = nums[n - i - 1];

            int diff = abs(a - b);

            int X = max({a, b, k - a, k - b});

            mppDi[diff].push_back(X);

            valXv.push_back(X);

        }


        int ProAns = n;

        sort(valXv.begin(), valXv.end());


        for (auto& entry : mppDi) {

            int diff = entry.first;

            int T = lower_bound(valXv.begin(), valXv.end(), diff) - valXv.begin();

            T = T * 2 + (n / 2 - T);


        for (int X : entry.second) {

                if (X < diff) {

                    T -= 2;

                } else {

                    T -= 1;

                }

            }

            ProAns = min(ProAns, T);

        }

        return ProAns;



    }
};
