class Solution {
public:
    int s[100010] {};
    int w[100010] {};
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        for (int i = 0, j = n - 1; i < j; i++, j--) {
            int a = min(nums[i], nums[j]);
            int b = max(nums[i], nums[j]);
            s[0]++;
            w[b - a]++;
            s[max(k - a, b) + 1]++;
        }
        int ans = INT_MAX;
        for (int i = 1; i <= k; i++) {
            s[i] += s[i - 1];
        }
        for (int i = 0; i <= k; i++) {
            s[i] -= w[i];
            ans = min(ans, s[i]);
        }
        return ans;
    }
};
