class Solution {
    fun minChanges(nums: IntArray, k: Int): Int {
        val n = nums.size
        val a = mutableMapOf<Int, Int>()
        val b = IntArray(n.shr(1))
        repeat(n.shr(1)) { i ->
            val v = abs(nums[i] - nums[n - i - 1])
            a[v] = (a[v] ?: 0) + 1
            b[i] = maxOf(nums[i], nums[n - i - 1], k - nums[i], k - nums[n - i - 1])
        }
        return a.map { (v, cnt) ->
            n.shr(1) - cnt + b.count { it < v }
        }.min()
    }
}
