class Solution {
public:
	int minChanges(vector<int>& nums, int k) {
		int n = nums.size();
		vector<int> cnt(k+10);
		//计算所有都变成间隔i的最少操作次数为cnt[i]
		for(int i = 0;i < n / 2;i++){
			int x = nums[i];
			int y = nums[n - i - 1];
			int t1 = k - min(x,y);
			int t2 = max(x,y);
			int mx = max(t1,t2);
			cnt[0]++;
			cnt[mx + 1]--;
			cnt[mx + 1] += 2;
			cnt[k + 1] -= 2;
			cnt[abs(x - y)]--;
			cnt[abs(x - y) + 1]++;
		}
		for(int i = 0;i <= k;i++){
			cnt[i + 1] += cnt[i];
		}
		int minn = n;
		for(int i = 0;i <= k;i++){
			//cout << i << " " << cnt[i] << endl;
			minn = min(minn,cnt[i]);
		}
		return minn;
	}
};
