class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> dp(k + 1, n / 2);
        vector<int> outofrange(k + 1, 0);
        for (int i = 0; i < n - i - 1; i++) {
            int dev = abs(nums[i] - nums[n - i - 1]);
            --dp[dev];
            int range = max(max(nums[i], nums[n - i - 1]),
                            k - min(nums[i], nums[n - i - 1]));
            if (range != k) {
                outofrange[range + 1] += 1;
            }
        }
        int sum = 0;
        for (int i = 0; i <= k; i++) {
            sum += outofrange[i];
            outofrange[i] = sum;
        }
        int ans = INT_MAX;
        for (int i = 0; i <= k; i++) {
            ans = min(ans, dp[i] + outofrange[i]);
        }
        return ans;
    }
};
