class Solution {
public:

    int solve(vector<long> &v,long x){
        int ind=lower_bound(v.begin(),v.end(),x)-v.begin();
        return ind;
    }

    int minChanges(vector<int>& nums, int k) {
          int n=nums.size();
        unordered_map<int,int> mp;
        vector<long> arr(n/2);


        for(int i=0;i<n/2;i++){
            int v=abs(nums[i]-nums[n-i-1]);
            mp[v]++;  
            int a=min(nums[i],nums[n-i-1]);
            int b=k-max(nums[i],nums[n-i-1]);

            int val=v+max(a,b);
            arr[i]=val;
       }
        sort(arr.begin(),arr.end());
        int ans=INT_MAX;
        for(auto &i:mp){
            long x=i.first;
            int c=i.second;  
            int r=solve(arr,x);
            cout<<"r="<<r<<" t=";
            int temp=n/2+r-c;
            cout<<temp<<" ";
            ans=min(ans,temp);

        }
        return ans;
    }
};
