class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> oneEnd;
        int n=nums.size();
        for(int i=0;i<n/2;i++)
        {
            int f1=max(nums[i],k-nums[i]);
            int f2=max(nums[n-i-1],k-nums[n-i-1]);
            oneEnd[max(f1,f2)]++;
        }
        map<int,int> already;
        for(int i=0;i<n/2;i++)
            already[abs(nums[i]-nums[n-i-1])]++;
        // for(auto &x:oneEnd)
        //     cout<<x.first<<" "<<x.second<<"\n";
        // cout<<"\n";
        //  for(auto &x:already)
        //     cout<<x.first<<" "<<x.second<<"\n";
        // cout<<"\n";
        int ans=n/2;
        int curr=n/2;
        for(int i=0;i<=k;i++)
        {
            ans=min(ans,curr-already[i]);
            curr+=oneEnd[i];
        }
        return ans;
    }
};
