#include<ext/pb_ds/assoc_container.hpp>
#include<ext/pb_ds/tree_policy.hpp>
using namespace __gnu_pbds;

typedef tree<int , null_type , less_equal<int>, rb_tree_tag, tree_order_statistics_node_update > pbds; //find_by_order() --> it , order_of_key()

class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        map<int,int>mp;

        int n = nums.size();

        for(int i = 0; i < n/2; i++) mp[abs(nums[i] - nums[n - i - 1])]++;

        pbds st;

        int ans = n;

        vector<pair<int,int>> v;
        for(int i = 0 ; i< n/2; i++){
            int x = min( nums[i]  , nums[n - i - 1]);
            int y = max( nums[i]  , nums[n - i - 1]);
            v.push_back({abs(nums[i] - nums[n - i - 1]) , max(y , k - x) } );
        }
        sort(v.begin() , v.end());

        for(int i = 0; i < n/2; i++){
            int y = 0;
            if( st.size() > 0 ) {
                y = st.order_of_key(v[i].first);
            }

            // cout << i << " " << y <<" " << n/2 - i - 1 + 2*y + (int)st.size() - y << endl;
            ans = min( ans , n/2 - i - 1 + 2*y + (int)st.size() - y - mp[v[i].first] + 1 ) ;
            st.insert(v[i].second);
        }


        return ans;

    }
};
