#include <vector>  
#include <map>  
#include <algorithm>  
#include <cmath>  
using namespace std;  

class Solution {  
public:  
    int minChanges(vector<int>& nums, int k) {  
        map<int, vector<int>> differenceMap; // Rename `m` to `differenceMap`  
        vector<int> maxValues; // Rename `v` to `maxValues`  

        // Calculate differences and corresponding maximum values  
        for (int i = 0; i < nums.size() / 2; i++) {  
            int firstElement = nums[i]; // Rename `a` to `firstElement`  
            int secondElement = nums[nums.size() - i - 1]; // Rename `b` to `secondElement`  
            int difference = abs(firstElement - secondElement); // Rename `diff` to `difference`  
            int maxValue = max({firstElement, secondElement, k - firstElement, k - secondElement}); // Rename `X` to `maxValue`  

            differenceMap[difference].push_back(maxValue); // Use `maxValue`  
            maxValues.push_back(maxValue); // Use `maxValues`  
        }  

        int minChangesRequired = nums.size(); // Rename `ans` to `minChangesRequired`  

        sort(maxValues.begin(), maxValues.end()); // Sort the maximum values  

        // Iterate through the difference map to find the minimum changes  
        for (auto &entry : differenceMap) {  
            int difference = entry.first; // Rename `val` to `entry`  
            int count = lower_bound(maxValues.begin(), maxValues.end(), difference) - maxValues.begin(); // Rename `T` to `count`  
            count = count * 2 + (nums.size() / 2 - count);  

            for (auto &value : entry.second) { // Rename `X` in the inner loop to `value`  
                if (value < difference) {  
                    count -= 2;  
                } else {  
                    count -= 1;  
                }  
            }  
            minChangesRequired = min(minChangesRequired, count); // Use `minChangesRequired`  
        }  

        return minChangesRequired; // Return the final answer  
    }  
};
