class Solution {
    public int minChanges(int[] nums, int k) {
        int n = nums.length;
        int[] thresh = new int[3*k + 5];
        int[] diff = new int[k + 1];

        for(int i = 0; i < nums.length/2; i++) {
            int a = nums[i], b = nums[nums.length - i - 1];
            diff[Math.abs(a - b)]++;
            // System.out.println(a + " " + b);
            int maxi = Math.max(Math.max(a, b) - 0, k - Math.min(a, b));
            thresh[maxi + 1]++;
        }

        for(int i = 1; i < thresh.length; i++) {
            thresh[i] += thresh[i - 1];
        }

        int ans = Integer.MAX_VALUE;
        for(int i = 0; i <= k; i++) {
            // System.out.println(i + " " + thresh[i] + " " + diff[i]);
            ans = Math.min(ans, thresh[i]*2 + (n/2 - thresh[i] - diff[i]));
        }
        return ans;
    }
}
