class Solution {
public:
static bool cmp(pair<int,int> a,pair<int,int> b) {
    if (a.first == b.first) return a.second < b.second;
    return a.first > b.first;
}
    int minChanges(vector<int>& nums, int k) {
        int cnt = 0,n = nums.size();
        vector<pair<int,int>>ans;
        map<int,int>mp;
        for (int i = 0; i < n / 2; i++) {
            int x = abs(nums[i] - nums[n - i - 1]);
            mp[x]++;
        }
        for (auto &i : mp) {
            ans.push_back({i.second,i.first});
        }
        sort(ans.begin(),ans.end(),cmp);
        int res = 10,tt = 1e9;
        for (int i = 0; i < ans.size(); i++) {
            int left = 0,right = n - 1,flag = 1,cnt = 0;
            while (left < right) {
                int x = abs(nums[right] - nums[left]);
                int mx = max(nums[right],nums[left]),mn = min(nums[right],nums[left]);
                if (x != ans[i].second) {
                    if (max(mx,k) < ans[i].second) {
                        flag = 0;
                        break;
                    }else if (max(mx,k) - mn >= ans[i].second || mx - 0 >= ans[i].second) cnt++;
                    else cnt += 2;
                }
                left++;
                right--;
            }
            if (flag == 1) tt = min(tt,cnt);
            if (flag == 1) res--;
            if (res == 0) return tt;
        }
        return tt;
    }
};
