class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        map<int,int> diff;
        vector<int> a(100005,0);
        for(int i=0;i<n/2;i++){
            int val = abs(nums[i]-nums[n-i-1]);
            diff[val]++;
            int ma = max(nums[i],nums[n-i-1]);
            int mi = min(nums[i],nums[n-i-1]);
            int mini = min(mi,min(abs(ma-k),min(abs(mi-k),min(abs(ma-0),abs(mi-0)))));
            if(mi<=k) mini = 0;
            int maxi = max(mi,max(abs(ma-k),max(abs(mi-k),max(abs(ma-0),abs(mi-0)))));
            a[mini]++;
            a[maxi+1]--;
        }
        for(int i=1;i<=100003;i++)
        {
            a[i]=a[i]+a[i-1];
        }        
        int ans = 1e8;
        for(auto &[u,v]:diff){
            int req = a[u]-v;
            int req2 = n/2-req-v;
            int temp = req+2*req2;
           // cout<<u<<" "<<req<<" "<<req2<<" "<<v<<endl;
            ans=min(ans,temp);
        }
        // for(int i=0;i<14;i++)
        //     cout<<a[i]<<" ";
        return ans;


    }
};
