class Solution {
public:
    int minChanges(vector<int>& v, int k) {
        int n = v.size();
        int len = n/2;
        vector<int> diff(k+5,0);
        mi mp;
        for(int i=0;i<len;i++){
            int x = v[i];
            int y = v[n-i-1];
            int minn = min(x,y);
            int maxx = max(x,y);
            int cur_d = maxx - minn;
            diff[0] += 1;
            diff[k+1] -= 1;
            mp[cur_d]++;
            int lim = k - minn + 1;
            x = k - x;
            y = k - y;
            int lim2 = k - min(x,y) + 1;
            lim = max(lim,lim2);
            diff[lim] += 1;
            diff[k+1] -= 1;
            //deb1(cur_d);
        }
        for(int i=1;i<=k;i++){
            diff[i] += diff[i-1];
        }
        //trace(diff);
        int ans = 1e9;
        for(int i=0;i<=k;i++){
            //deb2(i,mp[i]);
            //ans = min(ans,len-mp[i]);
            int temp = diff[i] - mp[i];
            ans = min(ans,temp);
        }

        return ans;

    }
};
