class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> diff2num;

        int n = nums.size();
        for(int i = 0; i < n / 2; i++) {
            int diff = abs(nums[i] - nums[n - i - 1]);
            if(diff2num.count(diff)) diff2num[diff]++;
            else diff2num[diff] = 1;
        }

        // int maxDiff = 0, maxNum = 0;
        // for(auto& [diff, num] : diff2num) {
        //     if(num > maxNum) maxDiff = diff;
        // }

        int ans = INT_MAX / 2;
        for(auto& [diff, num] : diff2num) {
            if(n / 2 - num > ans) continue;
            int res = 0;
            for(int i = 0; i < n / 2; i++) {
                if(abs(nums[i] - nums[n - i - 1]) == diff) continue;

                int min = nums[i], max = nums[n - i - 1];
                if(nums[i] > nums[n - i - 1]) {
                    min = nums[n - i - 1];
                    max = nums[i];
                }
                if(min + diff > k && max - diff < 0) res += 2;
                else res++;
                if(res > ans) break;
            }
            ans = min(ans, res);
        }

        return ans;
    }
};
