class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> arr;
        vector<int> v;
        for(int i = 0;i<nums.size()/2;i++){
            int a1 = nums[i],a2 = nums[nums.size()-i-1];
            int d = abs(a1-a2);
            int x = max({a1,a2,k-a1,k-a2});
            arr[d].push_back(x);
            v.push_back(x);
        }
        int ans = nums.size();
        sort(v.begin(),v.end());

        for(auto &val:arr){

            int d = val.first;
            int t = lower_bound(v.begin(),v.end(),d)-v.begin();
            t = t*2+(nums.size()/2-t);

            for(auto &x:val.second){
                if(x<d){
                    t-=2;
                }
                else{
                    t-=1;
                }
            }

            ans = min(ans,t);

        }

        return ans;
    }
};
