class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int,int> m;
        vector<int> v;
        int notposs=0;
        int ele;
        int n=nums.size();

        for(int i=0;i<n/2;i++){
            int diff=abs(nums[i]-nums[n-i-1]);
            m[diff]++;
            int maxi=max(nums[i],nums[n-i-1]);
            int mini=min(nums[i],nums[n-i-1]);
            int push=max(maxi-0,k-mini);
            v.push_back(push);

        }
        sort(v.begin(),v.end());
        int ans=INT_MAX;
        int sz=v.size();
        for(auto x:m){
            int l=0;
            int r=sz-1;
            while(l<=r){
                int mid=(l+r)/2;
                if(v[mid]>=x.first) r=mid-1;
                else l=mid+1;
            }
            int possi=(l*2+(sz-l-x.second));
            ans=min(ans,possi);
        }
        return ans;
    }
};
