class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>>mp;
        vector<int>temp;
        for(int i = 0;i<nums.size()/2;i++){
            int x = nums[i],y = nums[nums.size()-i-1];
            int d = abs(x-y);
            int maxi = max({x,y,k-x,k-y});
            mp[d].push_back(maxi);
            temp.push_back(maxi);
        }
        int ans = nums.size();
        sort(temp.begin(),temp.end());
        for(auto it:mp){
            int d = it.first;
            int val = lower_bound(temp.begin(),temp.end(),d)-temp.begin();
            val = val*2+(nums.size()/2-val);
            for(auto i:it.second){
                if(i<d)val-=2;
                else val-=1;
            }
            ans = min(ans,val);
        }
        return ans;
    }
};
