class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,int> mp ;
        int n = nums.size() ;
        vector<int> v ;
        for(int i = 0 ; i < n/2 ; i++ ){
            int x = nums[i] ;
            int y = nums[n-i-1] ; 
            mp[ abs(x-y) ]++ ;
            int mx = max(x, max(y, max( k-x, k-y ))) ;
            v.push_back(mx) ;
        }

        sort(v.begin(), v.end()) ;

        for(auto ele : v ) cout << ele << " " ;
        cout << endl ;
        int op = n/2 ; 
        for(int i = 0 ; i <= v[n/2 - 1 ]  ; i++){
            int smaller = lower_bound( v.begin(), v.end(), i) - v.begin() ;
            int bigger = v.size() - smaller ;

            bigger -= mp[i] ;
            op = min( op, smaller*2 + bigger ) ;
        }
        return op ;

    }
};

