class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> v;
        unordered_map<int,int> mp;
        int n=nums.size(),mn=1e9;
        for(int i=0;i<n/2;i++) {
            v.push_back(max(k-min(nums[i],nums[n-i-1]),max(nums[i],nums[n-i-1])));
            mp[(abs(nums[i]-nums[n-i-1]))]++;
        }
        sort(v.begin(),v.end());
        for(int i=0;i<=k;i++) {
            int x=lower_bound(v.begin(),v.end(),i)-v.begin();
            mn=min(mn,(n/2)-x+(x*2)-mp[i]);
        }
        return mn;
    }
};
