class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> cmap;
        vector<int> cv;
        for(int i = 0;i<nums.size()/2;i++){
            int a = nums[i],b = nums[nums.size()-i-1];
            int diff = abs(a-b);
            int maxos = max({a,b,k-a,k-b});
            cmap[diff].push_back(maxos);
            cv.push_back(maxos);
        }
        int ans = nums.size();
        sort(cv.begin(),cv.end());

        for(auto &val:cmap){

            int d = val.first;
            int T = lower_bound(cv.begin(),cv.end(),d)-cv.begin();
            T = T*2+(nums.size()/2-T);

            for(auto &maxos:val.second){
                if(maxos<d){
                    T-=2;
                }
                else{
                    T-=1;
                }
            }

            ans = min(ans,T);

        }

        return ans;

    }
};
