class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, vector<int>> diffMap; vector<int> maxVals;
        auto solve = [&](vector<int>& nums, int k) {
            for (int i = 0; i < nums.size() / 2; i++) {
                int n1 = nums[i], n2 = nums[nums.size() - i - 1], diff = abs(n1 - n2), maxV = max({n1, n2, k - n1, k - n2});
                diffMap[diff].push_back(maxV);
                maxVals.push_back(maxV);
            }
            sort(maxVals.begin(), maxVals.end());
        };
        auto solve2 = [&](int n) {
            int res = n;

            for (auto& entry : diffMap) {
                int diff = entry.first, aux = upper_bound(maxVals.begin(), maxVals.end(), diff - 1) - maxVals.begin();
                aux = aux * 2 + (n / 2 - aux);

                for (auto& maxV : entry.second) {
                    aux -= (maxV < diff) ? 2 : 1;
                }
                res = min(res, aux);
            }
            return res;
        };
        solve(nums, k); return solve2(nums.size());
    }
};
