class Solution:
    def minChanges(self, nums: List[int], k: int) -> int:
        n = len(nums)
        m = n // 2
        pre = [0 for _ in range(k + 1)]

        for i in range(m):
            a, b = nums[i], nums[n - i - 1]

            if a < b:
                a, b = b, a

            inter = max(k - b, a)
            pre[0] += 1
            pre[a - b] -= 1
            if a - b + 1 <= inter:
                pre[a - b + 1] += 1

            if inter + 1 <= k:
                pre[inter + 1] += 1

        ans, cur = n, 0
        for i in range(k + 1):
            cur += pre[i]
            ans = min(ans, cur)

        return ans
