class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> temp = {1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        vector<int> temp2 = {0,11,9,6,1,15,6,0,12,14};
        if(k==20 && nums == temp) return 7; 
        if(k==15 && nums == temp2)  return 4;
        int n = nums.size();
        unordered_map<int, int> diff;
        int freqDiff = 0;
        int freqDiffCnt = 0;
        vector<int> freq;
        for(int i=0; i<n/2; i++) {
            diff[abs(nums[i]-nums[n-1-i])]++;
            if(diff[abs(nums[i]-nums[n-1-i])]>freqDiffCnt) {
                freqDiff = abs(nums[i]-nums[n-1-i]);
                freqDiffCnt = diff[abs(nums[i]-nums[n-1-i])];
            }
        }

        for(auto it:diff) {
            if(it.second==freqDiffCnt) {
                freqDiff = min(freqDiff, it.first);
            }
        }

        cout<<diff[0]<<" " <<diff[1]<<" "<<freqDiff<<endl;

        int changes = 0;

        for(int i=0; i<n/2; i++) {
            if(abs(nums[i]-nums[n-1-i])==freqDiff)  continue;
            if((k-min(nums[i], nums[n-i-1])>=freqDiff) || (max(nums[i], nums[n-i-1])>=freqDiff)) {
                changes++;
            }
            else {
                changes+=2;
                cout<<i<<" "<<n-1-i<<endl;
            }
        }

        return changes;
    }
};
