#define lli long long
class Solution {
public:
    void update(lli ind, lli value, lli i, lli l, lli r, vector<lli> &seg)
{
    if (l == r)
    {
        seg[i]+= value;
        return;
    }
    lli mid = (l + r) / 2;
    if (ind <= mid)
    {
        update(ind, value, 2 * i + 1, l, mid, seg);
    }
    else
    {
        update(ind, value, 2 * i + 2, mid + 1, r, seg);
    }
    seg[i] = seg[2 * i + 1] + seg[2 * i + 2];
    return;
}
lli rsum(lli left, lli right, lli i, lli l, lli r, vector<lli> &seg)
{
    // cout << l << " " << r << endl;
    lli mid = (l + r) / 2;
    if (l >= left && r <= right)
    {
        return seg[i];
    }
    if (l > right || r < left)
    {
        return 0;
    }
    // lli a=update(left, right, value, 2 * i + 1, l, mid, seg, seg1);
    // lli b=update(left, right, value, 2 * i + 2, mid + 1, r, seg, seg1);
    return rsum(left, right, 2 * i + 1, l, mid, seg) + rsum(left, right, 2 * i + 2, mid + 1, r, seg);
}
    int minChanges(vector<int>& v, int k) {
        map<lli,lli>m;
        int n=v.size();
        vector<lli>seg(4*k+20);
        for(int i=0;i<n/2;i++){
            m[abs(v[i]-v[n-1-i])]++;
            lli ma=max(v[i],v[n-1-i]);
            lli mi=min(v[i],v[n-1-i]);
            //cout<<k-mi<<" "<<ma<<endl;
            update(max(k-mi,ma),1,0,0,k,seg);
        }
        lli ans=1e18;
        for(auto it:m){
            lli x=0;
            if(it.first>0){
                x=rsum(0,it.first-1,0,0,k,seg);
            }
            ans=min(ans,x+n/2-it.second);
        }
        return (int)(ans);
    }
};
