class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int> increase(k+1);
        unordered_map<int,int> dict;
        for (int i=0;i<n/2;i++){
            int bound=0;
            bound=max(bound,max(nums[i],k-nums[i]));
            bound=max(bound,max(nums[n-i-1],k-nums[n-i-1]));
            increase[bound]++;
            dict[abs(nums[i]-nums[n-i-1])]++;
        }
        int ans=INT_MAX;
        for (auto& p:dict){
            int cmp=p.first;
            int cur=n/2-p.second;
            for (int b=k/2;b<cmp;b++){
                cur+=increase[b];
            }
            ans=min(ans,cur);
        }
        return ans;
    }
};
