class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mpp;
        vector<int> temp;
        for(int i = 0;i<nums.size()/2;i++){
            int p = nums[i],q = nums[nums.size()-i-1];
            int diff = abs(p-q);
            int a = max({p,q,k-p,k-q});
            mpp[diff].push_back(a);
            temp.push_back(a);
        }
        int res = nums.size();
        sort(temp.begin(),temp.end());

        for(auto it:mpp){

            int d = it.first;
            int b = lower_bound(temp.begin(),temp.end(),d)-temp.begin();
            b = b*2+(nums.size()/2-b);

            for(auto &x:it.second){
                if(x<d){
                    b-=2;
                }
                else{
                    b-=1;
                }
            }

            res = min(res,b);

        }

        return res;

    }
};
