class Solution {
public:
    int minChanges(vector<int>& a, int k) {
        const int n = a.size();

        std::map<int, int> cnt;
        for (int i = 0; i < n / 2; ++i) {
            ++cnt[std::abs(a[i] - a[n - i - 1])];
        }

        int C = 0;
        std::vector<int> c(n / 2);
        std::set<std::pair<int, int>> A;
        for (int i = 0; i < n / 2; ++i) {
            int mn = std::min(a[i], a[n - i - 1]);
            int mx = std::max(a[i], a[n - i - 1]);
            int v = std::max(k - mn, mx);
            A.insert({v, i});
            // std::cout << v << " " << i << std::endl;
        }

        int ans = INT_MAX;
        for (int x = 0; x <= k; ++x) {
            while (!A.empty() && A.begin()->first < x) {
                auto [v, i] = *A.begin();
                A.erase(A.begin());
                ++C;
            }

            int cost = (n / 2) + C;
            if (cnt.contains(x)) cost -= cnt[x];
            ans = std::min(ans, cost);

            // std::cout << x << ": " << cost << " " << C << " " << cnt[x] << std::endl;
        }
        return ans;
    }
};
