class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mp;
        vector<int> v;
        int n=nums.size();

        int l=0,r=n-1;
    int ans =n;
        while(l<r){
            int diff=abs(nums[l]-nums[r]);
            int mx=max({nums[l],nums[r],k-nums[l],k-nums[r]});
            mp[diff].push_back(mx);
            v.push_back(mx);
            l++;
            r--;
        }


        sort(v.begin(),v.end());

        for(auto val:mp){

            int diff = val.first;
            int idx = lower_bound(v.begin(),v.end(),diff)-v.begin();
            idx = idx*2+(nums.size()/2-idx);

            for(auto X:val.second){
                if(X<diff){
                    idx-=2;
                }
                else{
                    idx-=1;
                }
            }

            ans = min(ans,idx);

        }

        return ans;

    }
};
