class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<pair<int,int>> vp;
        int n=nums.size();
        map<int,int> mp;
        vector<int> vec;
        for(int i=0;i<n/2;i++){
            int r=abs(nums[i]-nums[n-i-1]);
            int mini=min(nums[i],nums[n-i-1]);
            int maxi=max(nums[i],nums[n-i-1]);
            int rec=r+max(mini,k-maxi);
            vec.push_back(rec);
            mp[r]++;
        }
        sort(vec.begin(),vec.end());
        // for(auto x:vec){
        //     cout<<x<<" ";
        // }
        // cout<<endl;
        int ans=n;
        for(auto x:mp){
            int frq=x.second;
            int val=x.first;
            int lo=lower_bound(vec.begin(),vec.end(),val)-vec.begin();
            //cout<<val<<" "<<lo<<" "<<n/2 - lo<<" "<<frq<<endl;
            int left=lo*2;
            int right=(n/2)-lo-frq;
            //cout<<left+right<<endl;
            ans=min(ans,left+right);
        }
        //cout<<endl;
        return ans;
    }
};
