class Solution {
public:
//baingan best hai.........
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<int>> mappi;
        vector<int> arr;
        for(int i = 0;i<nums.size()/2;i++){
            int q = nums[i],r = nums[nums.size()-i-1];
            int minus = abs(q-r);
            int num = max({q,r,k-q,k-r});
            mappi[minus].push_back(num);
            arr.push_back(num);
        }
        int sol = nums.size();
        sort(arr.begin(),arr.end());

        for(auto &it:mappi){

            int minus = it.first;
            int tittu = lower_bound(arr.begin(),arr.end(),minus)-arr.begin();
            tittu = tittu*2+(nums.size()/2-tittu);

            for(auto &num:it.second){
                if(num<minus){
                    tittu-=2;
                }
                else{
                    tittu-=1;
                }
            }

            sol = min(sol,tittu);

        }

        return sol;

    }
};
