class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n=nums.size();
        vector<int>diff(k+1);
        vector<int>m1(k+1);
        for(int i=0; i<nums.size()/2; i++)
        {
            diff[abs(nums[i]-nums[n-i-1])]++;
            m1[max(max(nums[i],nums[n-i-1])-0,k-min(nums[i],nums[n-i-1]))]++;
        }
        int ans=INT_MAX;
        int c=0;
        for(int i=0; i<=k; i++)
        {
            ans=min(ans,n/2-diff[i]+c);
            c+=m1[i];
        }
        return ans;
    }
};
