#include <vector>
#include <map>
#include <algorithm>
#include <cmath>

#define X nums
#define A map
#define B vector
#define C int
#define D for
#define E abs
#define F max
#define G push_back
#define H sort
#define I lower_bound
#define J begin
#define K end
#define L size
#define M first
#define N second
#define O min

class Solution {
public:
    C minChanges(B<C>& X, C k) {
        A<C, B<C>> y;
        B<C> z;
        D(C i = 0; i < X.L() / 2; i++) {
            C p = X[i], q = X[X.L() - i - 1];
            C r = E(p - q);
            C s = F({p, q, k - p, k - q});
            y[r].G(s);
            z.G(s);
        }
        C t = X.L();
        H(z.J(), z.K());

        D(auto &u : y) {
            C r = u.M;
            C v = I(z.J(), z.K(), r) - z.J();
            v = v * 2 + (X.L() / 2 - v);

            D(auto &s : u.N) {
                if(s < r) {
                    v -= 2;
                } else {
                    v -= 1;
                }
            }

            t = O(t, v);
        }

        return t;
    }
};
