class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        map<int,vector<pair<int,int>>>mp;
        int n=nums.size()-1;
        vector<int>vec(k+2,0);
                for(int i=0;i<nums.size()/2;i++)
                {
                    mp[abs(nums[i]-nums[n-i])].push_back({nums[i],nums[n-i]});
                    int x=max(nums[i],nums[n-i]);
                    int y=min(nums[i],nums[n-i]);
                    int r=max(x-0,k-y);
                    // cout<<r<<" ";
                    vec[r]++;
                }
                cout<<endl;
                for(int i=k-1;i>=0;i--)
                {
                    vec[i]+=vec[i+1];
                }
                int res=INT_MAX;
                for(auto i:mp)
                {
                    int l=nums.size()/2-vec[i.first+1]-i.second.size();
                    int count=0;

                    for(int j=0;j<i.second.size();j++)
                    {
                            int x=max(i.second[j].first,i.second[j].second);
                    int y=min(i.second[j].first,i.second[j].second);
                    int r=max(x-0,k-y);
                    if(r>=i.first)
                    count++;
                    }
                    int m=vec[i.first]-count;
                    int s=nums.size()/2-i.second.size();
                    res=min(res,2*s-m);
                }

       return res;
    }
};
