const getDifferenceCountHash = (nums: number[]) => {
    let initialDifferenceCountHash = {}

    for (let index = 0; index < nums.length / 2; index++) {
        const difference = Math.abs(nums[index] - nums[nums.length - index - 1]);
        if (!initialDifferenceCountHash[difference]) initialDifferenceCountHash[difference] = 1;
        else initialDifferenceCountHash[difference] = initialDifferenceCountHash[difference] + 1;
    }

    return initialDifferenceCountHash;
}

const getPossibleXs = (hash: Record<number, number>) => Object.entries(hash).sort((entryOne: [string, number], entryTwo: [string, number]) => entryTwo[1] - entryOne[1]).map((entry: [string, number]) => [+entry[0], entry[1]]);

const getOperationCount = (numOne: number, numTwo: number, X: number, k: number) => {
    if (Math.abs(numOne - numTwo) === X) return 0;

    if (numTwo + X <= k) return 1;
    if (numTwo - X >= 0 && numTwo - X <= k) return 1;

    if (numOne + X <= k) return 1;
    if (numOne - X >= 0 && numOne - X <= k) return 1;

    if (X <= k) return 2;

    return -1;
}

function minChanges(nums: number[], k: number): number {
    let initialDifferenceCountHash = getDifferenceCountHash(nums);
    let possibleXs = getPossibleXs(initialDifferenceCountHash);
    let minOperationCount = Infinity;

    for (let index = 0; index < possibleXs.length; index++) {
        let operationCount = 0, X = possibleXs[index][0];
        let minPossibleOperationCountForX = (nums.length - (possibleXs[index][1] * 2)) / 2;

        if (minPossibleOperationCountForX > minOperationCount) continue;

        // console.log("Checking for possible X", X);
        for (let bIndex = 0; bIndex < nums.length / 2; bIndex++) {
            const currentOperationCount = getOperationCount(nums[bIndex], nums[nums.length - bIndex - 1], X, k)

            // console.log("Operation count for pair", nums[bIndex], nums[nums.length - bIndex - 1], "is", currentOperationCount)
            if (currentOperationCount === -1) {
                operationCount = Infinity;
                break;
            }

            operationCount += currentOperationCount;
        }
        // console.log("Total operation count is ", operationCount);
        minOperationCount = Math.min(minOperationCount, operationCount);
        // console.log("Min operation count is ", minOperationCount);
    }

    return minOperationCount;
};
