class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        int n = nums.size();
        vector<int> v(k + 3);
        for(int i = 0, j = n - 1; i < n / 2; i++, j--){
            if(nums[i] > nums[j]) swap(nums[i], nums[j]);
            int ma = max(nums[j], k - nums[i]);
            int mi = nums[j] - nums[i];
            v[mi] -= 1; v[mi + 1] += 1;
            v[0] += 1; v[ma + 1] -= 1;
            v[ma + 1] += 2, v[k + 1] -= 2;

        }int ans = 1e7;
        for(int i = 0; i <= k; i++) {
            if(i > 0) v[i] += v[i - 1];
            ans = min(ans, v[i]);
        }
        return ans;

    }
};
