class Solution {
public:
  int minChanges(vector<int>& nums, int k) {
    int n = (int)nums.size();
    int res = 1e9;

    vector<int> vec(k + 10, 0);

    for(int i = 0; i < n / 2; i++) {
      int mini = min(nums[i], nums[n - 1 - i]);
      int maxi = max(nums[i], nums[n - 1 - i]);


      int diff = maxi - mini;

      vec[0]++;
      vec[diff]--;

      vec[diff + 1]++;
      int maxDiffWithOneChg = max(maxi, k - mini);
      vec[maxDiffWithOneChg + 1]++;
    }

    for(int i = 1; i <= k; i++) {
      vec[i] += vec[i - 1];
    }

    for(int i = 0; i <= k; i++) {
      res = min(res, vec[i]);
    }
    return (int)res;
  }
};
