class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        //replace from 0 to k
        // nums is even length
        // delta of 
        // greedy linear check
        unordered_map<int, int> counts;
        int mx = 0;
        int mxVal = 0;
        int n = nums.size();
        for(int i = 0; i < n/2; ++i){
            int val = abs(nums[i] - nums[n - i - 1]);
            counts[val]++;
        }
        int mxa = 0;
        int mxVala = 0;
        for(pair<int, int> p: counts){
            if(p.second > mx){
                    mxa = mx;
                    mxVala = mxVal;
                    mx = p.second;
                    mxVal = p.first;  
            }
            else if(p.second == mx){
                if(p.first <= mxVal){
                    mxa = mx;
                    mxVala = mxVal;
                    mx = p.second;
                    mxVal = p.first; 
                }
            }
        }
        int count = 0;
        int counta = 0;
        // change 1 or both numbers (if small is mx val from k, or large is mxVal from 0)
        for(int i = 0; i < n/2; ++i){
            int val = abs(nums[i] - nums[n - i - 1]);
            if(val != mxVal){
                if(nums[i] > nums[n - i - 1]){
                    if(nums[i] - 0 >= mxVal || k - nums[n - i - 1] >= mxVal) count++;
                    else count+=2;
                }
                else{
                    if(k - nums[i]  >= mxVal || nums[n - i - 1] - 0 >= mxVal) count++;
                    else count += 2;
                }
            }
            if(val != mxVala){
                if(nums[i] > nums[n - i - 1]){
                    if(nums[i] - 0 >= mxVala || k - nums[n - i - 1] >= mxVala) counta++;
                    else counta+=2;
                }
                else{
                    if(k - nums[i]  >= mxVala || nums[n - i - 1] - 0 >= mxVala) counta++;
                    else counta += 2;
                }
            }
        }
        return min(count, counta);
    }
};
