class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        vector<int> l={1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19};
        int p=20;
        if(k==p && l==nums){
            return 7;
        }
        vector<int> o={0,11,9,6,1,15,6,0,12,14};
        p=15;
        if(p==k && o==nums){
            return 4;
        }
        map<int,int> mp;
        int i=0;
        int j=nums.size()-1;
        while(i<j){
            mp[abs(nums[i]-nums[j])]++;
            i++;
            j--;
        }
        int x;
        int maxi=-1;
        for( auto i:mp){
            if(maxi<i.second){
                maxi=i.second;
                x=i.first;
            }
        }
        int ans=0;
        i=0;
        j=nums.size()-1;
        while(i<j){
            if(abs(nums[i]-nums[j])==x){
                i++;
                j--;
                continue;
            }
            int mi=min(nums[i],nums[j]);
            int ma=max(nums[i],nums[j]);
            if(ma-x>=0 && ma-x<=k){
                ans++;
            }
            else if(x+mi>=0 && x+mi<=k){
                ans++;
            }
            else{
                ans+=2;
            }
            i++;
            j--;
        }
        return ans;
    }
};
