class Solution {
public:
    int minChanges(vector<int>& nums, int k) {

        int n=nums.size();
        map<int,int> mp;
        for(int i=0;i<n/2;i++)
        {
            mp[abs(nums[i]-nums[n-i-1])]++;
        }
        n/=2;
        int ans=INT_MAX;
        // set<int> ele;
        priority_queue<pair<int,int>> pq;
        for(auto num:mp)
        {
              pq.push({num.second,num.first});

        }


        int ans1=INT_MAX;
        while(!pq.empty()){
            int count=0;
            pair<int,int> temp=pq.top();
            pq.pop();
            if(ans1<=(n-temp.first))break;
            int el=temp.second;
        for(int i=0;i<n;i++)
        {

            if((abs(nums[i]-nums[n*2-i-1]))!=el)
            {
                int val=abs(nums[i]-nums[n-i-1]);
                // cout<<"val"<<val<<endl;
                int pos1=nums[i]+el;
                int pos2=nums[n*2-i-1]+el;
                int pos3=nums[i]-el;
                int pos4=nums[n*2-i-1]-el;
                if(pos1<=k || pos2<=k || pos3>=0 || pos4>=0)
                {
                    count+=1;
                }
                else
                {
                    count+=2;
                }
                // cout<<count<<" "<<i<<endl;

            }
        }
            cout<<el<<count<<endl;
            ans1=min(count,ans1);
            // cout<<nums[i]<<endl;
        }
        return ans1;

    }
};
