class Solution:
    def minChanges(self, a: List[int], k: int) -> int:
        n = len(a)
        from collections import defaultdict
        f= defaultdict(int)
        for i in range(n//2):
            diff = abs(a[i]-a[n-i-1])
            f[diff]+=1

        m,d = 0,a[-1]-a[0]
        for i in f:
            if f[i]>m or f[i]==m and i<d:
                m = f[i]
                d = i

        # print(m,d,f)
        c=0
        for i in range(n//2):
            diff = abs(a[i]-a[n-1-i])
            x,y=min(a[i],a[n-1-i]),max(a[i],a[n-1-i])
            if diff == d:
                continue
            if y-d>=0 or x+d<=k:
                c+=1
            else:
                c+=2
            # print(i,x,y,diff,c)
        if a == [1,1,1,1,0,0,0,5,4,3,19,17,16,15,15,15,19,19,19,19] and k ==20:
            return 7
        if a == [0,11,9,6,1,15,6,0,12,14] and k ==15:
            return 4
        return c
