class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map <int,vector <int>> mp;
        vector <int> t;
        int n = nums.size();
        int i = 0, j = n-1, ans = n;
        while(i < j){
            int val = abs(nums[j]-nums[i]);
            int x = max({nums[i], nums[j], k-nums[i], k-nums[j]});
            mp[val].push_back(x);
            t.push_back(x);
            i++; j--;
        }
        sort(t.begin(), t.end());
        for(auto it : mp){
            int d = it.first;
            int b = lower_bound(t.begin(), t.end(), d)-t.begin();
            b = b*2 + (n/2 - b);
            for(auto z : it.second){
                if(z < d)b-=2;
                else b--;
            }
            ans = min(ans, b);
        }
        return ans;
    }
};
