class Solution {
public:
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> a;      // a[i]：差为i的pair的个数
        int b[100005];                  // b[i]：调整范围小于等于i的个数
        memset(b, 0, sizeof(b));
        int n = nums.size();
        for (int i=0; i<n/2; ++i) {
            int diff = abs(nums[i] - nums[n-i-1]);
            ++a[diff];
            int range1 = max(k-nums[i], nums[i]);
            int range2 = max(k-nums[n-i-1], nums[n-i-1]);
            int range = max(range1, range2);
            ++b[range];
        }

        for (int i=1; i<k; ++i)
            b[i]+= b[i-1];

        int ans = n/2;
        for (int i=0; i<=k/2; ++i) 
            ans = min(ans, n/2 - a[i]);

        for (int i=k/2+1; i<=k; ++i)
            ans = min(ans, n/2-a[i] + b[i-1]);

        return ans;
    }
};
