class Solution {
    int ans;
    public int minChanges(int[] nums, int k) {
        ans=nums.length;
        HashMap<Integer,Integer> map=new HashMap<>();
        int l=0,r=nums.length-1;
        while(l<r){
            int diff=Math.abs(nums[l]-nums[r]);
            if(diff>k){
                l++;
                r--;
                continue;}
            map.put(diff,map.getOrDefault(diff,0)+1);
            l++;
            r--;
        }
        ArrayList<int[]> adj=new ArrayList<>();
        for(int i:map.keySet()){
            adj.add(new int[]{i,map.get(i)});// diff, freq
        }
        if(adj.size()==1)return 0;
        Collections.sort(adj,(a,b)->a[1]-b[1]);// lower freq first
        int maxa=adj.get(adj.size()-1)[1];
        l=adj.size()-1;
        int count=0;
        while(l>=0 && (adj.get(l)[1]==maxa || count<500)){
            possible(nums,k,adj.get(l)[0]);
            l--;
            count++;
        }
        return ans;
    }

    public void possible(int nums[],int k,int target){
        int count=0;
        int l=0,r=nums.length-1;
        while(l<r){
            int diff=Math.abs(nums[l]-nums[r]);
            if(diff==target){
                l++;
                r--;
            continue;
            }
                int gap=target-diff;
            if(nums[l]<nums[r]){
                if(nums[l]-gap>=0 || nums[r]+gap<=k){
                    count++;
                }
                else count+=2;
            }
            else{
                if(nums[l]+gap<=k || nums[r]-gap>=0){
                    count++;
                }
                else count+=2;
            }

            l++;
            r--;
        }
        ans=Math.min(ans,count);

    }
}
