class Solution {
public:
    typedef pair<int, int> PII;
    int minChanges(vector<int>& nums, int k) {
        unordered_map<int, int> hash;
        int n = nums.size();
        for (int i = 0; i < n / 2; i ++ )
            hash[abs(nums[i] - nums[n - 1 - i])] ++ ;
        vector<PII> pr;
        for (auto x: hash) pr.push_back({x.second, x.first});
        sort(pr.begin(), pr.end());
        int t = -1, res = n / 2;
        for (int i = 0; i < pr.size(); i ++ ) {
            if (pr[i].first == t) continue;
            t = pr[i].first;
            int ans = 0, dx = pr[i].second; 
            for (int j = 0; j < n / 2; j ++ ) {
                if (abs(nums[j] - nums[n - 1 - j]) == dx) continue;
                int cmin = min(nums[j], nums[n - 1 - j]), cmax = max(nums[j], nums[n - 1 - j]);
                if (dx > k - cmin && dx > cmax) ans += 2;
                else ans += 1;
            }
            res = min(res, ans);
        }
        return res;
    }
};
