package main

import (
	"math"
	"sort"
)

func minChanges(nums []int, k int) int {
	diffMap := make(map[int][]int)
	var maxValues []int
	n := len(nums)

	for i := 0; i < n/2; i++ {
		a, b := nums[i], nums[n-i-1]
		diff := int(math.Abs(float64(a - b)))
		maxValue := max(a, b, k-a, k-b)
		diffMap[diff] = append(diffMap[diff], maxValue)
		maxValues = append(maxValues, maxValue)
	}

	result := n
	sort.Ints(maxValues)

	for diff, values := range diffMap {
		requiredChanges := sort.Search(len(maxValues), func(i int) bool { return maxValues[i] >= diff })
		requiredChanges = requiredChanges*2 + (n/2 - requiredChanges)

		for _, maxValue := range values {
			if maxValue < diff {
				requiredChanges -= 2
			} else {
				requiredChanges -= 1
			}
		}

		if requiredChanges < result {
			result = requiredChanges
		}
	}

	return result
}

func max(nums ...int) int {
	maxVal := nums[0]
	for _, num := range nums {
		if num > maxVal {
			maxVal = num
		}
	}
	return maxVal
}
