class Solution {
public:
    typedef long long LL;
    const LL INFLL = 0x3f3f3f3f3f3f3f3fll;
    long long maximumScore(vector<vector<int>>& g) {
        int n = g.size();
        vector<vector<LL>> s(n + 1, vector<LL>(n, 0ll));
        for (int i = 0; i < n; ++i) {
            for (int j = 0; j < n; ++j) {
                s[i + 1][j] = s[i][j] + g[i][j];
            }
        }
        vector<vector<LL>> f(n + 1, vector<LL>(n + 1, -INFLL));
        f[0] = vector<LL>(n + 1, 0ll);
        for (int i = 1; i < n; ++i) {
            vector<vector<LL>> tf(n + 1, vector<LL>(n + 1, -INFLL));
            for (int k = 0; k <= n; ++k) {
                LL t = 0;
                for (int j = 0; j <= n; ++j) {
                    t = max(t, f[j][k]);
                }
                for (int l = 0; l < k; ++l) {
                    tf[k][l] = max(tf[k][l], t + s[k][i] - s[l][i]);
                }
            }
            for (int j = 0; j <= n; ++j) {
                for (int k = 0; k <= n; ++k) {
                    tf[k][k] = max(tf[k][k], f[j][k]);
                }
            }
            for (int k = 0; k <= n; ++k) {
                LL t = -INFLL;
                for (int j = 0; j <= n; ++j) {
                    t = max(t, f[j][k] - s[max(j, k)][i - 1]);
                }
                for (int l = k + 1; l <= n; ++l) {
                    tf[k][l] = max(tf[k][l], s[l][i - 1] + t);
                }
            }
            f = tf;
        }
        LL res = 0;
        for (int i = 0; i <= n; ++i) {
            for (int j = 0; j <= n; ++j) {
                res = max(res, f[i][j]);
            }
        }
        return res;
    }
};
