class Solution {
public:
    long long maximumScore(vector<vector<int>>& grid) {
        if(grid[0].size()==1){
            int Max=0;
            for(int i = 1;i<grid.size();i++){
                Max=max(Max,grid[Max][0]);
            }
            return 0;
        }
        long long dp[105][105][105];
        long long sum=0;
        dp[0][0][0]=0;

        long long ans=0;
        for(int i = 0;i<grid.size();i++){
            sum+=grid[i][1];
            dp[0][0][i+1]=sum;
           // ans=max(ans,sum);
         //   printf("%d\n",sum);
            for(int j = 1;j<=grid.size();j++){
                dp[0][j][i+1]=-1e18;
                dp[0][j][0]=-1e18;
            }
        }
        for(int i = 1;i<grid[0].size();i++){
            for(int j = 0;j<=grid.size();j++){
                long long sum=0;
                long long val[105];
                val[0]=dp[i-1][0][j];
                for(int k=1;k<=grid.size();k++){
                    val[k]=max(val[k-1]+(k>j?grid[k-1][i-1]:0),dp[i-1][k][j]);
                   // printf("?%lld ",val[k]);
                }
                //printf("\n");
                long long Max=0;
                for(int k = grid.size();k>=0;k--){
                    Max=max(Max,dp[i-1][k][j]);
                    val[k]=max(val[k],Max);
                    //printf("?%lld ",val[k]);
                }
              //  printf("\n");
                dp[i][j][0]=Max;
                ans=max(ans,Max);
                for(int k = 1;k<=grid.size();k++){
                    if(i+1!=grid[0].size())
                    sum+=grid[k-1][i+1];
                    if(k<=j)sum-=grid[k-1][i];
                    dp[i][j][k]=sum+val[k];
                    ans=max(ans,sum+val[k]);
                //    printf("%d %d %d %lld %lld\n",i,j,k,dp[i][j][k],sum);
                }
            }
        }
        return ans;
    }
};
