using ll = long long int;
const int N = 105;

ll dp[N][N];
ll pd[N][N];
ll prf[N][N];

class Solution {
public:
    long long maximumScore(vector<vector<int>>& a) {
        int n = a.size(),m = a[0].size();

		memset(dp,0,sizeof(dp));
		memset(pd,0,sizeof(dp));
		memset(prf,0,sizeof(dp));


        for(int i = 0;i < n;i++){
            for(int j = 0;j < m;j++){
                prf[i][j] = a[i][j];
                if(i){
                    prf[i][j] += prf[i-1][j];
                }
            }
        }

        auto tell = [&](int j,int l,int r){
            if(l > r){
                return 0ll;
            }
            return prf[r][j] - (l == 0 ? 0 : prf[l-1][j]);
        };

        for(int j = 1;j < m;j++){
			memset(pd,0,sizeof(pd));
            for(int i1 = 0;i1 <= n;i1++){
				for(int i3 = i1;i3 <= n;i3++){
					for(int i2 = 0;i2 <= n;i2++){
						ll ext = 0;
						int i4 = i2;
						if(i2 >= i3){
							ext += tell(j-1,i3,i2-1);
						}
						if(i1 >= i2){
							ext += tell(j,i2,i1-1);
							i4 = i1;
						}
						pd[i2][i4] = max(pd[i2][i4],dp[i1][i3] + ext);
					}
				}
            }
            swap(dp,pd);
        }
        ll ans = 0;

        for(int i = 0; i <= n; i++){
			for(int j = 0; j <= n; j++){
				ans = max(ans,dp[i][j]);
			}
		}

		return ans;
    }
};
