#define _int64 long long

_int64 d[2][110][110];
_int64 sum[110][110];

class Solution {
public:
    long long maximumScore(vector<vector<int>>& a) {
        int i,j,k,l,m,n,now,o;
        _int64 tmp,ans;
        m=a.size();
        n=a[0].size();
        for (i=0;i<=m;i++)
            for (j=0;j<=m;j++)
                d[0][i][j]=-1;
        for (i=0;i<=m;i++)
            d[0][i][i]=0;
        for (i=0;i<n;i++)
        {
            sum[i][0]=0;
            for (j=0;j<m;j++)
                sum[i][j+1]=sum[i][j]+a[j][i];
        }
        now=0;
        for (i=1;i<n;i++)
        {
            for (j=0;j<=m;j++)
                for (k=0;k<=m;k++)
                    d[1-now][j][k]=-1;
            for (j=0;j<=m;j++)
            {
                for (k=0;k<=m;k++)
                {
                    if (d[now][j][k]==-1) continue;
                    for (l=0;l<=m;l++)
                    {
                        tmp=d[now][j][k];
                        if (j<l)
                        {
                            if (k<l)
                                tmp+=sum[i-1][l]-sum[i-1][k];
                            o=l;
                        }
                        else if (j>l)
                        {
                            tmp+=sum[i][j]-sum[i][l];
                            o=j;
                        }
                        else o=l;
                        d[1-now][l][o]=max(d[1-now][l][o],tmp);
                    }
                }
            }
            now=1-now;
        }
        ans=0;
        for (j=0;j<=m;j++)
            for (k=0;k<=m;k++)
                ans=max(ans,d[now][j][k]);
        return ans;
    }
};
