class Solution {
public:
    long long maximumScore(vector<vector<int>>& g) {
        int n = g.size();
        if (n == 1) return 0;
        long long a[105][105] = {0};
        long long b[105][105] = {0};
        long long c[105][105] = {0};
        long long d[105][105] = {0};
        for (int i = 0; i < n; i++) {
            c[i][n] = 0;
            for (int j = n - 1; j >= 0; j--)
                c[i][j] = c[i][j + 1] + g[j][i];
        }
        for (int i = 0; i <= n; i++)
            for (int j = 0; j <= n; j++) {
                if (i < j) a[i][j] = c[0][i] - c[0][j];
                else a[i][j] = 0;
            }
        for (int i = 2; i < n; i++) {
            for (int j = 0; j <= n; j++)
                for (int k = 0; k <= n; k++) {
                    long long e = a[j][k] + c[i - 1][k];
                    int t = max(k, j);
                    for (int l = 0; l <= t; l++) {
                        b[k][l] = max(e - c[i - 1][t], b[k][l]);
                    }
                    for (int l = t + 1; l <= n; l++)
                        b[k][l] = max(e - c[i - 1][l], b[k][l]);
                }
            for (int j = 0; j <= n; j++)
                for (int k = 0; k <= n; k++)
                    a[j][k] = b[j][k];
            for (int j = 0; j <= n; j++)
                for (int k = 0; k <= n; k++)
                    b[j][k] = 0;
        }
        long long ret = 0;
        for (int i = 0; i <= n; i++)
            for (int j = 0; j <= n; j++)
                ret = max(a[i][j] + c[n - 1][j] - c[n - 1][max(i, j)], ret);
        return ret;
    }
};
